import React, { Component } from 'react';
import './App.css';
import MovieRow from './MovieRow.js'
import $ from 'jquery'
class App extends Component {
  constructor(props) {
    super(props)
    this.state ={}


    this.performSearch()
  }

  performSearch() {
    console.log("seraching.......")
    const urlString = "https://api.themoviedb.org/3/movie/550?api_key=6c356a9956b05290257ead8ec4192700"
    $.ajax({
      url:urlstring,
      success: (searchResults) => {
        console.log("Fetched Data Successfully")
      },
      error:(xhr,status,err) => {
        console.log("Failed to load Data")
      }


    })
  }
  render() {
    return (
      <div>
        <table className="titleBar">
          <tbody>
            <tr>
              <td>
                <img alt="app icon" width="50"  src="logo.svg"/>
              </td>
              <td>
                <h2>MoviesDB Search</h2>
              </td>
            </tr>
          </tbody>
        </table>

        <input style={{
          fontSize:18,
          display:'block',
          width:"99%",
          paddingTop:8,
          paddingBottom:8,
          paddingLeft:16
        }} placeholder="Enter Search"/>


        {this.state.rows}

      </div>
    );
  }
}

export default App;
